import React, { useState } from 'react';
import { useDropzone } from 'react-dropzone';
import pdfToText from 'react-pdftotext';

const FileUpload = () => {
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const { getRootProps, getInputProps } = useDropzone({
    onDrop: (acceptedFiles) => {
        console.log(acceptedFiles[0]);
      if (acceptedFiles) {
        pdfToText(acceptedFiles[0])
          .then((text) => setUploadedFiles(text))
          .catch((error) => console.error("Failed to extract text from pdf"));
      }
      console.log(uploadedFiles);
    },
  });
//TO DO : Customize and Style this Drag and Drop to Upload box as you wantüßë‚Äçüíªüòä
  return (
    <div {...getRootProps()}>
      <input className="form-control-alternative"
                  disabled
                  placeholder="Drag and drop files here or click to browse."
                  type="text" {...getInputProps()} />
      {/* <p>Drag and drop files here or click to browse.</p> */}
      <ul>
        {/* {uploadedFiles.map((file) => (
          <li key={file.name}>{file.name}</li>
        ))} */}
        <li>{uploadedFiles}</li>
      </ul>
    </div>
  );
};
export default FileUpload;
